    f.write(r.content)
</code></pre> <pre><code>const pdfRes = await fetch(`http://127.0.0.1:8000/clearview/${data.id}`);
const blob = await pdfRes.blob();
const url = URL.createObjectURL(blob);
// open or download the blob as needed
</code></pre>

curlPythonJavaScript <pre><code># Save the audio file and print the record ID from the header
curl -X POST https://clearwayapi.tech/audio \
  -H "Content-Type: application/json" \
  -d '{"input": "https://www.bbc.com/news/articles/example"}' \
  -o audio.mp3 \
  -D headers.txt

# Extract the record ID
grep -i "x-content-id" headers.txt
</code></pre> <pre><code>import httpx

r = httpx.post(
    "https://clearwayapi.tech/audio",
    json={"input": "https://www.bbc.com/news/articles/example"},
    timeout=120,
)
r.raise_for_status()

record_id = r.headers["x-content-id"]
print("Record ID:", record_id)

with open("audio.mp3", "wb") as f:
    f.write(r.content)
print("Saved audio.mp3")
</code></pre> <pre><code>const res = await fetch("https://clearwayapi.tech/audio", {
  method: "POST",
  headers: { "Content-Type": "application/json" },
  body: JSON.stringify({ input: "https://www.bbc.com/news/articles/example" }),
});

if (!res.ok) throw new Error(`Error: ${res.status}`);

const recordId = res.headers.get("x-content-id");
console.log("Record ID:", recordId);

// Download the audio
const blob = await res.blob();
const url = URL.createObjectURL(blob);
const a = document.createElement("a");
a.href = url;
a.download = "audio.mp3";
a.click();
</code></pre>

